// src/components/Transaction/NovaTransacaoForm.js
import React, { useEffect, useState, useRef } from 'react';
import { criarTransacao, atualizarTransacao, obterCategorias, obterTags } from '../../api.js';
import './NovaTransacaoForm.css';

const NovaTransacaoForm = ({ onSuccess, onClose, transacao }) => {
  // Se transacao for fornecida (edição), pré-preenche; senão, inicializa com valores vazios
  const [tipo, setTipo] = useState(transacao ? transacao.tipo : 'gasto');
  const [descricao, setDescricao] = useState(transacao ? transacao.descricao : '');
  const [data, setData] = useState(transacao ? transacao.data.split('T')[0] : '');
  const [valorTotal, setValorTotal] = useState(transacao ? transacao.valor.toString() : '');
  
  const descricaoSuggestions = ['Compra de pizza', 'Pagamento de conta', 'Arbitragem'];
  
  const [categorias, setCategorias] = useState([]);
  const [tags, setTags] = useState([]);
  const [globalTags, setGlobalTags] = useState(transacao ? transacao.tags : {});
  
  const [pagamentos, setPagamentos] = useState(transacao ? transacao.pagamentos : [{ pessoa: '', valor: '', paymentTags: {} }]);
  const pessoaSuggestions = ['Eu', 'Alisson', 'Emerson'];
  
  const descricaoRef = useRef(null);
  const dataRef = useRef(null);
  const valorRef = useRef(null);
  
  const handleFocus = (e) => {
    e.target.scrollIntoView({ behavior: 'smooth', block: 'center' });
  };
  
  useEffect(() => {
    async function fetchData() {
      try {
        const cats = await obterCategorias();
        setCategorias(cats);
        const initGlobal = {};
        cats.forEach(cat => {
          // Se já existe valor (edição), mantém, senão inicia com array vazio
          initGlobal[cat.nome] = globalTags[cat.nome] || [];
        });
        setGlobalTags(initGlobal);
        const tgs = await obterTags();
        setTags(tgs);
      } catch (error) {
        console.error('Erro ao carregar categorias ou tags:', error);
      }
    }
    fetchData();
    if (!transacao) {
      const hoje = new Date().toISOString().split('T')[0];
      setData(hoje);
    }
  }, [transacao]);
  
  const getTagsByCategory = () => {
    return tags.reduce((acc, tag) => {
      if (tag.categoria) {
        const catKey = tag.categoria;
        if (!acc[catKey]) {
          acc[catKey] = [];
        }
        acc[catKey].push(tag);
      }
      return acc;
    }, {});
  };
  
  const tagsPorCategoria = getTagsByCategory();
  
  const handleGlobalTagChange = (categoria, selectedOptions) => {
    setGlobalTags(prev => ({
      ...prev,
      [categoria]: selectedOptions
    }));
  };
  
  const handlePagamentoChange = (index, field, value) => {
    const novosPagamentos = [...pagamentos];
    novosPagamentos[index][field] = value;
    setPagamentos(novosPagamentos);
  };
  
  const handlePagamentoTagChange = (index, categoria, selectedOptions) => {
    const novosPagamentos = [...pagamentos];
    if (!novosPagamentos[index].paymentTags) {
      novosPagamentos[index].paymentTags = {};
    }
    novosPagamentos[index].paymentTags[categoria] = selectedOptions;
    setPagamentos(novosPagamentos);
  };
  
  const addPagamento = () => {
    setPagamentos([...pagamentos, { pessoa: '', valor: '', paymentTags: {} }]);
  };
  
  const removePagamento = (index) => {
    const novosPagamentos = pagamentos.filter((_, i) => i !== index);
    setPagamentos(novosPagamentos);
  };
  
  const validatePagamentos = () => {
    const soma = pagamentos.reduce((acc, pag) => {
      const valor = parseFloat(pag.valor || 0);
      return acc + (isNaN(valor) ? 0 : valor);
    }, 0);
    return parseFloat(valorTotal) === soma;
  };
  
  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.ctrlKey && e.key === 's') {
        e.preventDefault();
        handleSubmit(e, false);
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  });
  
  const setHoje = () => {
    const hoje = new Date().toISOString().split('T')[0];
    setData(hoje);
  };
  
  const setOntem = () => {
    const ontem = new Date(Date.now() - 86400000).toISOString().split('T')[0];
    setData(ontem);
  };
  
  const handleValorTotalChange = (e) => {
    const raw = e.target.value;
    setValorTotal(raw);
    if (pagamentos.length === 1) {
      handlePagamentoChange(0, 'valor', raw);
    }
  };
  
  const handleSubmit = async (e, closeModal = true) => {
    e.preventDefault();
    if (!validatePagamentos()) {
      alert('A soma dos pagamentos deve ser igual ao valor total.');
      return;
    }
    const novaTransacao = {
      tipo,
      descricao,
      data,
      valor: Number(parseFloat(valorTotal).toFixed(2)), // Valor formatado com 2 casas decimais
      tags: globalTags,
      pagamentos: pagamentos.map(pag => ({
        pessoa: pag.pessoa,
        valor: Number(parseFloat(pag.valor).toFixed(2)), // Valor formatado com 2 casas decimais
        tags: pag.paymentTags
      }))
    };
    try {
      if (transacao) {
        await atualizarTransacao(transacao.id, novaTransacao);
      } else {
        await criarTransacao(novaTransacao);
      }
      alert('Transação registrada com sucesso!');
      if (onSuccess) onSuccess();
      if (closeModal) {
        onClose();
      } else {
        // Limpa os campos para continuar
        setDescricao('');
        setValorTotal('');
        setGlobalTags(prev => {
          const novo = { ...prev };
          Object.keys(novo).forEach(key => novo[key] = []);
          return novo;
        });
        setPagamentos([{ pessoa: '', valor: '', paymentTags: {} }]);
        if (descricaoRef.current) {
          descricaoRef.current.focus();
        }
      }
    } catch (error) {
      console.error('Erro ao criar/atualizar transação:', error);
      alert('Erro ao criar/atualizar transação.');
    }
  };
  
  return (
    <div className="nova-transacao-form-container">
      <h2>{transacao ? 'Editar Transação' : 'Nova Transação'}</h2>
      <form onSubmit={(e) => handleSubmit(e, true)}>
        <div className="form-grid">
          <div className="left-column">
            <div className="form-section">
              <label>Tipo:</label>
              <select 
                value={tipo} 
                onChange={e => setTipo(e.target.value)} 
                required 
                onFocus={handleFocus}
              >
                <option value="gasto">Gasto</option>
                <option value="recebivel">Recebível</option>
              </select>
            </div>
            <div className="form-section">
              <label>Descrição:</label>
              <input 
                type="text" 
                value={descricao} 
                onChange={e => setDescricao(e.target.value)} 
                required 
                list="descricao-suggestions"
                onFocus={handleFocus}
                ref={descricaoRef}
              />
              <datalist id="descricao-suggestions">
                {descricaoSuggestions.map((sug, idx) => (
                  <option key={idx} value={sug} />
                ))}
              </datalist>
            </div>
            <div className="form-section">
              <label>Data:</label>
              <input 
                type="date" 
                value={data} 
                onChange={e => setData(e.target.value)} 
                required 
                onFocus={handleFocus}
                ref={dataRef}
              />
              <div className="date-shortcuts">
                <button type="button" onClick={setHoje}>Hoje</button>
                <button type="button" onClick={setOntem}>Ontem</button>
              </div>
            </div>
            <div className="form-section">
              <label>Valor Total:</label>
              <input 
                type="text" 
                value={valorTotal} 
                onChange={handleValorTotalChange} 
                required 
                onFocus={handleFocus}
                ref={valorRef}
              />
            </div>
          </div>
          <div className="right-column">
            <div className="form-section global-tags">
              <h3>Tags Globais</h3>
              {categorias.map(cat => (
                <div key={cat.id} className="tag-dropdown-group">
                  <label>{cat.nome}:</label>
                  <select multiple
                    value={globalTags[cat.nome] || []}
                    onChange={e => {
                      const selected = Array.from(e.target.selectedOptions, option => option.value);
                      handleGlobalTagChange(cat.nome, selected);
                    }}
                    onFocus={handleFocus}
                  >
                    {(tags.filter(t => t.categoria === cat.nome)).map(tag => (
                      <option key={tag.id} value={tag.nome}>
                        {tag.nome}
                      </option>
                    ))}
                  </select>
                </div>
              ))}
            </div>
            <div className="form-section pagamentos-section">
              <h3>Pagamentos</h3>
              {pagamentos.map((pagamento, index) => (
                <div key={index} className="pagamento-item">
                  <div className="form-section">
                    <label>Pessoa:</label>
                    <input 
                      type="text" 
                      value={pagamento.pessoa} 
                      onChange={e => handlePagamentoChange(index, 'pessoa', e.target.value)} 
                      required 
                      list="pessoa-suggestions"
                      onFocus={handleFocus}
                    />
                    <datalist id="pessoa-suggestions">
                      {pessoaSuggestions.map((sug, idx) => (
                        <option key={idx} value={sug} />
                      ))}
                    </datalist>
                  </div>
                  <div className="form-section">
                    <label>Valor:</label>
                    <input 
                      type="text" 
                      value={pagamento.valor} 
                      onChange={e => handlePagamentoChange(index, 'valor', e.target.value)} 
                      required 
                      onFocus={handleFocus}
                    />
                  </div>
                  <div className="form-section payment-tags">
                    <h4>Tags para Pagamento</h4>
                    {categorias.map(cat => (
                      <div key={cat.id} className="tag-dropdown-group">
                        <label>{cat.nome}:</label>
                        <select multiple
                          value={(pagamento.paymentTags && pagamento.paymentTags[cat.nome]) || []}
                          onChange={e => {
                            const selected = Array.from(e.target.selectedOptions, option => option.value);
                            handlePagamentoTagChange(index, cat.nome, selected);
                          }}
                          onFocus={handleFocus}
                        >
                          {(tags.filter(t => t.categoria === cat.nome)).map(tag => (
                            <option key={tag.id} value={tag.nome}>
                              {tag.nome}
                            </option>
                          ))}
                        </select>
                      </div>
                    ))}
                  </div>
                  <button type="button" onClick={() => removePagamento(index)}>Remover Pagamento</button>
                  <hr />
                </div>
              ))}
              <button type="button" onClick={addPagamento}>Adicionar Pagamento</button>
            </div>
          </div>
        </div>
        <div className="form-buttons">
          <button type="submit" className="submit-btn" onClick={(e) => handleSubmit(e, true)}>
            {transacao ? 'Atualizar e Fechar' : 'Salvar e Fechar'}
          </button>
          <button type="button" className="submit-btn" onClick={(e) => handleSubmit(e, false)}>
            {transacao ? 'Atualizar e Continuar' : 'Salvar e Continuar'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default NovaTransacaoForm;
