// src/components/Transaction/TransactionCard.js
import React from 'react';
import './TransactionCard.css';

const TransactionCard = ({ transacao }) => {
  const { descricao, data, valor, tipo, tags, pagamentos } = transacao;
  const formattedDate = new Date(data).toLocaleDateString('pt-BR', { timeZone: 'UTC' });
  const formattedValor = `R$${valor}`;

  const statusTags = tags && tags["Status da transação"] ? tags["Status da transação"] : [];
  const metodoTags = tags && tags["Método de Pagamento"] ? tags["Método de Pagamento"] : [];

  return (
    <div className="transaction-card">
      <div className="transaction-header">
        <h3 className="transaction-title">{descricao}</h3>
        <p className="transaction-info">
          {formattedDate} &ndash; {formattedValor} &ndash; {tipo}
        </p>
      </div>
      <hr className="divider" />
      <div className="transaction-tags">
        {statusTags.length > 0 && (
          <div className="tag-group">
            <span className="tag-label">Status:</span>
            {statusTags.map((t, idx) => (
              <span key={idx} className="tag-chip status-chip">{t}</span>
            ))}
          </div>
        )}
        {metodoTags.length > 0 && (
          <div className="tag-group">
            <span className="tag-label">Método:</span>
            {metodoTags.map((t, idx) => (
              <span key={idx} className="tag-chip metodo-chip">{t}</span>
            ))}
          </div>
        )}
      </div>
      <hr className="divider" />
      <div className="transaction-payments">
        <h4 className="payments-title">Pagamentos:</h4>
        <table className="payments-table">
          <thead>
            <tr>
              <th>Pessoa</th>
              <th>Valor</th>
              <th>Tags</th>
            </tr>
          </thead>
          <tbody>
            {pagamentos && pagamentos.map((pag, idx) => {
              let pagTags = '';
              if (pag.tags && typeof pag.tags === 'object') {
                pagTags = Object.keys(pag.tags)
                  .map(cat => `${cat}: ${pag.tags[cat].join(', ')}`)
                  .join(' | ');
              }
              return (
                <tr key={idx}>
                  <td>{pag.pessoa}</td>
                  <td>R${pag.valor}</td>
                  <td>{pagTags || '—'}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default TransactionCard;
