// src/components/Transaction/PainelCentral.js
import React, { useEffect, useState } from 'react';
import { obterTransacoes, obterRelatorio } from '../../api.js';
import './PainelCentral.css';

const PainelCentral = () => {
  const [transacoes, setTransacoes] = useState([]);
  const [relatorio, setRelatorio] = useState(null);
  const [sortOption, setSortOption] = useState('recentes'); // 'recentes', 'data_desc' ou 'data_asc'

  const carregarTransacoes = async () => {
    try {
      const dados = await obterTransacoes();
      let transacoesOrdenadas = dados.transacoes || [];
      if (sortOption === 'recentes') {
        // Ordena pelo campo createdAt se existir, senão usa o timestamp do _id
        transacoesOrdenadas = transacoesOrdenadas.sort((a, b) => {
          const dateA = new Date(a.createdAt || (a._id && new Date(parseInt(a._id.toString().substring(0,8), 16)*1000)));
          const dateB = new Date(b.createdAt || (b._id && new Date(parseInt(b._id.toString().substring(0,8), 16)*1000)));
          return dateB - dateA;
        });
      } else if (sortOption === 'data_desc') {
        // Ordena pela data da transação (descendente)
        transacoesOrdenadas = transacoesOrdenadas.sort((a, b) => new Date(b.data) - new Date(a.data));
      } else if (sortOption === 'data_asc') {
        // Ordena pela data da transação (ascendente)
        transacoesOrdenadas = transacoesOrdenadas.sort((a, b) => new Date(a.data) - new Date(b.data));
      }
      setTransacoes(transacoesOrdenadas);
    } catch (error) {
      console.error('Erro ao carregar transações:', error);
    }
  };

  const carregarRelatorio = async () => {
    try {
      const dados = await obterRelatorio();
      setRelatorio(dados);
    } catch (error) {
      console.error('Erro ao carregar relatório:', error);
    }
  };

  useEffect(() => {
    carregarTransacoes();
    carregarRelatorio();
  }, [sortOption]);

  return (
    <div className="painel-central-container">
      <h2 className="card-title">Dashboard</h2>
      {relatorio ? (
        <div className="card-content dashboard-summary">
          <p><strong>Total Gastos:</strong> R${relatorio.totalGastos}</p>
          <p><strong>Total Recebimentos:</strong> R${relatorio.totalRecebimentos}</p>
          <p><strong>Saldo Geral:</strong> R${relatorio.saldoGeral}</p>
        </div>
      ) : (
        <div className="card-content dashboard-summary">
          <p>Carregando relatório...</p>
        </div>
      )}

      <div className="sort-options">
        <label>Ordenar por:</label>
        <select value={sortOption} onChange={(e) => setSortOption(e.target.value)}>
          <option value="recentes">Lançadas Recentemente</option>
          <option value="data_desc">Por Data (Descendente)</option>
          <option value="data_asc">Por Data (Ascendente)</option>
        </select>
      </div>

      <h2 className="card-title">Transações Recentes</h2>
      {transacoes.length > 0 ? (
        <div className="transacoes-list">
          {transacoes.map(transacao => (
            <div key={transacao.id} className="transacao-card">
              <div className="transacao-header">
                <h3>{transacao.descricao}</h3>
                <p>
                  <span>{new Date(transacao.data).toLocaleDateString()}</span> |{' '}
                  <span>Valor: R${transacao.valor}</span> |{' '}
                  <span>Tipo: {transacao.tipo}</span>
                </p>
                {transacao.tags && typeof transacao.tags === 'object' && Object.keys(transacao.tags).length > 0 && (
                  <div className="transacao-tags">
                    {Object.keys(transacao.tags).map((cat) => (
                      <span key={cat} className="tag-chip">
                        {cat}: {transacao.tags[cat].join(', ')}
                      </span>
                    ))}
                  </div>
                )}
              </div>
              {transacao.pagamentos && transacao.pagamentos.length > 0 && (
                <div className="transacao-pagamentos">
                  <h4>Pagamentos</h4>
                  <table>
                    <thead>
                      <tr>
                        <th>Pessoa</th>
                        <th>Valor</th>
                        <th>Tags</th>
                      </tr>
                    </thead>
                    <tbody>
                      {transacao.pagamentos.map((pag, idx) => (
                        <tr key={idx}>
                          <td>{pag.pessoa}</td>
                          <td>R${pag.valor}</td>
                          <td>
                            {pag.tags && typeof pag.tags === 'object' && Object.keys(pag.tags).length > 0 
                              ? Object.keys(pag.tags).map(cat => (
                                  <span key={cat} className="tag-chip">
                                    {cat}: {pag.tags[cat].join(', ')}
                                  </span>
                                ))
                              : '—'}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          ))}
        </div>
      ) : (
        <div className="card-content no-transactions">
          <p>Nenhuma transação encontrada.</p>
          <button className="cta-add">Adicionar sua primeira transação</button>
        </div>
      )}
    </div>
  );
};

export default PainelCentral;
