// src/components/Tag/TagSelector.js
import React, { useEffect, useState } from 'react';
import { obterTags } from '../../api.js';
import './TagSelector.css';

const TagSelector = ({ onSelectionChange }) => {
  const [tags, setTags] = useState([]);
  const [selected, setSelected] = useState([]);

  useEffect(() => {
    async function fetchTags() {
      try {
        const dados = await obterTags();
        setTags(dados);
      } catch (error) {
        console.error('Erro ao carregar tags:', error);
      }
    }
    fetchTags();
  }, []);

  const handleCheckboxChange = (tagName) => {
    let newSelected;
    if (selected.includes(tagName)) {
      newSelected = selected.filter(name => name !== tagName);
    } else {
      newSelected = [...selected, tagName];
    }
    setSelected(newSelected);
    onSelectionChange(newSelected);
  };

  return (
    <div className="tag-selector">
      <h3>Selecione as Tags</h3>
      {tags.length > 0 ? (
        <div className="tag-list-selector">
          {tags.map(tag => (
            <label key={tag._id || tag.id} className="tag-option">
              <input
                type="checkbox"
                value={tag.nome}
                checked={selected.includes(tag.nome)}
                onChange={() => handleCheckboxChange(tag.nome)}
              />
              {tag.nome}
            </label>
          ))}
        </div>
      ) : (
        <p>Nenhuma tag dispon√≠vel.</p>
      )}
    </div>
  );
};

export default TagSelector;
