// src/components/Tag/TagManagement.js
import React, { useEffect, useState } from 'react';
import { obterTags, criarTag, atualizarTag, excluirTag, obterCategorias, criarCategoria, atualizarCategoria, excluirCategoria } from '../../api.js';
import './TagManagement.css';

const TagManagement = ({ onClose }) => {
  const [activeTab, setActiveTab] = useState('tags'); // 'tags' ou 'categorias'
  
  // Estados para Tags
  const [tags, setTags] = useState([]);
  const [novoNomeTag, setNovoNomeTag] = useState('');
  const [novaDescricaoTag, setNovaDescricaoTag] = useState('');
  const [novoCategoriaTag, setNovoCategoriaTag] = useState('');
  const [editandoTagId, setEditandoTagId] = useState(null);
  const [editNomeTag, setEditNomeTag] = useState('');
  const [editDescricaoTag, setEditDescricaoTag] = useState('');
  const [editCategoriaTag, setEditCategoriaTag] = useState('');
  
  // Estados para Categorias
  const [categorias, setCategorias] = useState([]);
  const [novoNomeCategoria, setNovoNomeCategoria] = useState('');
  const [novaDescricaoCategoria, setNovaDescricaoCategoria] = useState('');
  const [editandoCategoriaId, setEditandoCategoriaId] = useState(null);
  const [editNomeCategoria, setEditNomeCategoria] = useState('');
  const [editDescricaoCategoria, setEditDescricaoCategoria] = useState('');
  
  // Carregar dados
  const carregarTags = async () => {
    try {
      const dados = await obterTags();
      setTags(dados);
    } catch (error) {
      console.error('Erro ao carregar tags:', error);
      alert('Erro ao carregar tags.');
    }
  };

  const carregarCategorias = async () => {
    try {
      const dados = await obterCategorias();
      setCategorias(dados);
    } catch (error) {
      console.error('Erro ao carregar categorias:', error);
      alert('Erro ao carregar categorias.');
    }
  };

  useEffect(() => {
    carregarTags();
    carregarCategorias();
  }, []);

  // Funções para Tags
  const nomeTagDuplicado = (nome, id = null) => {
    return tags.some(tag => tag.nome.toLowerCase() === nome.toLowerCase() && tag.id !== id);
  };

  const handleAdicionarTag = async () => {
    if (!novoNomeTag.trim() || !novoCategoriaTag.trim()) {
      alert('Os campos nome e categoria são obrigatórios para a tag.');
      return;
    }
    if (nomeTagDuplicado(novoNomeTag)) {
      alert('Essa tag já existe.');
      return;
    }
    try {
      await criarTag({ nome: novoNomeTag.trim(), descricao: novaDescricaoTag.trim(), categoria: novoCategoriaTag.trim() });
      setNovoNomeTag('');
      setNovaDescricaoTag('');
      setNovoCategoriaTag('');
      carregarTags();
      alert('Tag criada com sucesso!');
    } catch (error) {
      console.error('Erro ao criar tag:', error);
      alert('Erro ao criar tag.');
    }
  };

  const handleEditarTag = (tag) => {
    setEditandoTagId(tag.id);
    setEditNomeTag(tag.nome);
    setEditDescricaoTag(tag.descricao || '');
    setEditCategoriaTag(tag.categoria);
  };

  const handleSalvarEdicaoTag = async () => {
    if (!editNomeTag.trim() || !editCategoriaTag.trim()) {
      alert('O nome e a categoria da tag são obrigatórios.');
      return;
    }
    if (nomeTagDuplicado(editNomeTag, editandoTagId)) {
      alert('Já existe outra tag com esse nome.');
      return;
    }
    try {
      await atualizarTag(editandoTagId, { nome: editNomeTag.trim(), descricao: editDescricaoTag.trim(), categoria: editCategoriaTag.trim() });
      setEditandoTagId(null);
      setEditNomeTag('');
      setEditDescricaoTag('');
      setEditCategoriaTag('');
      carregarTags();
      alert('Tag atualizada com sucesso!');
    } catch (error) {
      console.error('Erro ao atualizar tag:', error);
      alert('Erro ao atualizar tag.');
    }
  };

  const handleExcluirTag = async (id) => {
    if (window.confirm('Tem certeza que deseja excluir essa tag?')) {
      try {
        await excluirTag(id);
        carregarTags();
        alert('Tag excluída com sucesso!');
      } catch (error) {
        console.error('Erro ao excluir tag:', error);
        alert('Erro ao excluir tag.');
      }
    }
  };

  // Funções para Categorias
  const nomeCategoriaDuplicado = (nome, id = null) => {
    return categorias.some(cat => cat.nome.toLowerCase() === nome.toLowerCase() && cat.id !== id);
  };

  const handleAdicionarCategoria = async () => {
    if (!novoNomeCategoria.trim()) {
      alert('O nome da categoria é obrigatório.');
      return;
    }
    if (nomeCategoriaDuplicado(novoNomeCategoria)) {
      alert('Essa categoria já existe.');
      return;
    }
    try {
      await criarCategoria({ nome: novoNomeCategoria.trim(), descricao: novaDescricaoCategoria.trim() });
      setNovoNomeCategoria('');
      setNovaDescricaoCategoria('');
      carregarCategorias();
      alert('Categoria criada com sucesso!');
    } catch (error) {
      console.error('Erro ao criar categoria:', error);
      alert('Erro ao criar categoria.');
    }
  };

  const handleEditarCategoria = (categoria) => {
    setEditandoCategoriaId(categoria.id);
    setEditNomeCategoria(categoria.nome);
    setEditDescricaoCategoria(categoria.descricao || '');
  };

  const handleSalvarEdicaoCategoria = async () => {
    if (!editNomeCategoria.trim()) {
      alert('O nome da categoria é obrigatório.');
      return;
    }
    if (nomeCategoriaDuplicado(editNomeCategoria, editandoCategoriaId)) {
      alert('Já existe outra categoria com esse nome.');
      return;
    }
    try {
      await atualizarCategoria(editandoCategoriaId, { nome: editNomeCategoria.trim(), descricao: editDescricaoCategoria.trim() });
      setEditandoCategoriaId(null);
      setEditNomeCategoria('');
      setEditDescricaoCategoria('');
      carregarCategorias();
      alert('Categoria atualizada com sucesso!');
    } catch (error) {
      console.error('Erro ao atualizar categoria:', error);
      alert('Erro ao atualizar categoria.');
    }
  };

  const handleExcluirCategoria = async (id) => {
    if (window.confirm('Tem certeza que deseja excluir essa categoria?')) {
      try {
        await excluirCategoria(id);
        carregarCategorias();
        alert('Categoria excluída com sucesso!');
      } catch (error) {
        console.error('Erro ao excluir categoria:', error);
        alert('Erro ao excluir categoria.');
      }
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="modal-close" onClick={onClose}>X</button>
        <div className="tab-header">
          <button className={activeTab === 'tags' ? 'active' : ''} onClick={() => setActiveTab('tags')}>Tags</button>
          <button className={activeTab === 'categorias' ? 'active' : ''} onClick={() => setActiveTab('categorias')}>Categorias</button>
        </div>
        {activeTab === 'tags' ? (
          <div className="tab-content">
            <h2>Gerenciar Tags</h2>
            <div className="tag-list">
              {tags.length > 0 ? (
                <ul>
                  {tags.map(tag => (
                    <li key={tag.id}>
                      {editandoTagId === tag.id ? (
                        <div className="edit-tag">
                          <input
                            type="text"
                            value={editNomeTag}
                            onChange={(e) => setEditNomeTag(e.target.value)}
                            placeholder="Nome da tag"
                          />
                          <input
                            type="text"
                            value={editDescricaoTag}
                            onChange={(e) => setEditDescricaoTag(e.target.value)}
                            placeholder="Descrição (opcional)"
                          />
                          <input
                            type="text"
                            value={editCategoriaTag}
                            onChange={(e) => setEditCategoriaTag(e.target.value)}
                            placeholder="Categoria"
                          />
                          <button onClick={handleSalvarEdicaoTag}>Salvar</button>
                          <button onClick={() => setEditandoTagId(null)}>Cancelar</button>
                        </div>
                      ) : (
                        <div className="view-tag">
                          <span>
                            <strong>{tag.nome}</strong>{tag.descricao && ` - ${tag.descricao}`} ({tag.categoria})
                          </span>
                          <div className="acoes-tag">
                            <button onClick={() => handleEditarTag(tag)}>Editar</button>
                            <button onClick={() => handleExcluirTag(tag.id)}>Excluir</button>
                          </div>
                        </div>
                      )}
                    </li>
                  ))}
                </ul>
              ) : (
                <p>Nenhuma tag encontrada.</p>
              )}
            </div>
            <div className="nova-tag">
              <h3>Adicionar Nova Tag</h3>
              <input
                type="text"
                value={novoNomeTag}
                onChange={(e) => setNovoNomeTag(e.target.value)}
                placeholder="Nome da tag"
              />
              <input
                type="text"
                value={novaDescricaoTag}
                onChange={(e) => setNovaDescricaoTag(e.target.value)}
                placeholder="Descrição (opcional)"
              />
              <input
                type="text"
                value={novoCategoriaTag}
                onChange={(e) => setNovoCategoriaTag(e.target.value)}
                placeholder="Categoria"
              />
              <button onClick={handleAdicionarTag}>Adicionar</button>
            </div>
          </div>
        ) : (
          <div className="tab-content">
            <h2>Gerenciar Categorias</h2>
            <div className="categoria-list">
              {categorias.length > 0 ? (
                <ul>
                  {categorias.map(cat => (
                    <li key={cat.id}>
                      {editandoCategoriaId === cat.id ? (
                        <div className="edit-categoria">
                          <input
                            type="text"
                            value={editNomeCategoria}
                            onChange={(e) => setEditNomeCategoria(e.target.value)}
                            placeholder="Nome da categoria"
                          />
                          <input
                            type="text"
                            value={editDescricaoCategoria}
                            onChange={(e) => setEditDescricaoCategoria(e.target.value)}
                            placeholder="Descrição (opcional)"
                          />
                          <button onClick={handleSalvarEdicaoCategoria}>Salvar</button>
                          <button onClick={() => setEditandoCategoriaId(null)}>Cancelar</button>
                        </div>
                      ) : (
                        <div className="view-categoria">
                          <span>
                            <strong>{cat.nome}</strong>{cat.descricao && ` - ${cat.descricao}`}
                          </span>
                          <div className="acoes-categoria">
                            <button onClick={() => handleEditarCategoria(cat)}>Editar</button>
                            <button onClick={() => handleExcluirCategoria(cat.id)}>Excluir</button>
                          </div>
                        </div>
                      )}
                    </li>
                  ))}
                </ul>
              ) : (
                <p>Nenhuma categoria encontrada.</p>
              )}
            </div>
            <div className="nova-categoria">
              <h3>Adicionar Nova Categoria</h3>
              <input
                type="text"
                value={novoNomeCategoria}
                onChange={(e) => setNovoNomeCategoria(e.target.value)}
                placeholder="Nome da categoria"
              />
              <input
                type="text"
                value={novaDescricaoCategoria}
                onChange={(e) => setNovaDescricaoCategoria(e.target.value)}
                placeholder="Descrição (opcional)"
              />
              <button onClick={handleAdicionarCategoria}>Adicionar</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default TagManagement;
