// src/components/Layout/LayoutPrincipal.js
import React, { useState } from 'react';
import PainelEsquerdo from '../Transaction/PainelEsquerdo';
import PainelCentral from '../Transaction/PainelCentral';
import Relatorio from '../Relatorio/Relatorio';
import './LayoutPrincipal.css';

const LayoutPrincipal = () => {
  const [mostrarEsquerdo, setMostrarEsquerdo] = useState(true);
  // Removemos o toggle do painel direito, pois ele agora conterá os relatórios fixos.
  
  const toggleEsquerdo = () => {
    setMostrarEsquerdo(prev => !prev);
  };

  return (
    <div className="layout-container">
      <div className="painel painel-esquerdo">
        <button className="botao-toggle" onClick={toggleEsquerdo}>
          {mostrarEsquerdo ? 'Ocultar Lançamento' : 'Mostrar Lançamento'}
        </button>
        <div className={`conteudo-painel ${!mostrarEsquerdo ? 'oculto' : ''}`}>
          <PainelEsquerdo />
        </div>
      </div>
      <div className="painel painel-central">
        <PainelCentral />
      </div>
      <div className="painel painel-direito">
        <Relatorio />
      </div>
    </div>
  );
};

export default LayoutPrincipal;
