// src/App.js

import React, { useState } from 'react';
import LayoutPrincipal from './components/Layout/LayoutPrincipal.js';
import TagManagement from './components/Tag/TagManagement'; // Se estiver movendo TagManagement para a pasta Tag
import './App.css';

function App() {
  const [mostrarTagManagement, setMostrarTagManagement] = useState(false);

  return (
    <div className="App">
      <header className="app-header">
        <h1>Controle de Gastos</h1>
        <button onClick={() => setMostrarTagManagement(true)} style={{ marginTop: '10px' }}>
          Gerenciar Tags
        </button>
      </header>
      <LayoutPrincipal />
      {mostrarTagManagement && (
        <TagManagement onClose={() => setMostrarTagManagement(false)} />
      )}
    </div>
  );
}

export default App;
