// src/components/PainelDireito.js

import React, { useState } from 'react';
import { obterTransacoes } from '../api.js';

const PainelDireito = () => {
  const [dataInicio, setDataInicio] = useState('');
  const [dataFim, setDataFim] = useState('');
  const [pessoa, setPessoa] = useState('');
  const [transacoesFiltradas, setTransacoesFiltradas] = useState([]);

  const aplicarFiltros = async () => {
    try {
      const params = {
        dataInicio: dataInicio || undefined,
        dataFim: dataFim || undefined,
        pessoa: pessoa || undefined
      };
      const dados = await obterTransacoes(params);
      setTransacoesFiltradas(dados.transacoes || []);
    } catch (error) {
      console.error('Erro ao aplicar filtros:', error);
    }
  };

  return (
    <div>
      <h2>Filtros</h2>
      <div>
        <label>Data Início:</label>
        <input type="date" value={dataInicio} onChange={(e) => setDataInicio(e.target.value)} />
      </div>
      <div>
        <label>Data Fim:</label>
        <input type="date" value={dataFim} onChange={(e) => setDataFim(e.target.value)} />
      </div>
      <div>
        <label>Pessoa:</label>
        <input type="text" value={pessoa} onChange={(e) => setPessoa(e.target.value)} placeholder="Nome da pessoa" />
      </div>
      <button onClick={aplicarFiltros}>Aplicar Filtros</button>
      {transacoesFiltradas.length > 0 && (
        <div>
          <h3>Transações Filtradas</h3>
          <ul>
            {transacoesFiltradas.map(t => (
              <li key={t.id}>
                <strong>ID:</strong> {t.id} - <strong>Tipo:</strong> {t.tipo} - <strong>Valor:</strong> R${t.valor} - <strong>Data:</strong> {t.data} - <strong>Pessoa:</strong> {t.pessoa}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default PainelDireito;
