// src/components/PainelCentral.js

import React, { useEffect, useState } from 'react';
import { obterTransacoes, obterRelatorio } from '../api.js';

const PainelCentral = () => {
  const [transacoes, setTransacoes] = useState([]);
  const [relatorio, setRelatorio] = useState(null);

  const carregarTransacoes = async () => {
    try {
      const dados = await obterTransacoes();
      setTransacoes(dados.transacoes || []);
    } catch (error) {
      console.error('Erro ao carregar transações:', error);
    }
  };

  const carregarRelatorio = async () => {
    try {
      const dados = await obterRelatorio();
      setRelatorio(dados);
    } catch (error) {
      console.error('Erro ao carregar relatório:', error);
    }
  };

  useEffect(() => {
    carregarTransacoes();
    carregarRelatorio();
  }, []);

  return (
    <div>
      <h2>Dashboard</h2>
      {relatorio ? (
        <div className="relatorio">
          <h3>Resumo</h3>
          <p><strong>Total Gastos:</strong> R${relatorio.totalGastos}</p>
          <p><strong>Total Recebimentos:</strong> R${relatorio.totalRecebimentos}</p>
          <p><strong>Saldo Geral:</strong> R${relatorio.saldoGeral}</p>
          <h4>Detalhes por Pessoa:</h4>
          <ul className="relatorio-lista">
            {relatorio.relatorioPorPessoa &&
              Object.entries(relatorio.relatorioPorPessoa).map(([nome, dados]) => (
                <li key={nome}>
                  <strong>{nome}:</strong> Gasto Total: R${dados.gastoTotal}, Recebido: R${dados.recebido}, A Pagar: R${dados.pagar}, Gasto Líquido: R${dados.gastoLiquido}
                </li>
              ))}
          </ul>
        </div>
      ) : (
        <p>Carregando relatório...</p>
      )}
      <hr />
      <h2>Transações Recentes</h2>
      {transacoes.length > 0 ? (
        <ul>
          {transacoes.map(transacao => (
            <li key={transacao.id}>
              <strong>ID:</strong> {transacao.id} - <strong>Tipo:</strong> {transacao.tipo} - <strong>Valor:</strong> R${transacao.valor} - <strong>Data:</strong> {transacao.data} - <strong>Pessoa:</strong> {transacao.pessoa}
              <br />
              <strong>Tags:</strong> {transacao.tags ? transacao.tags.join(', ') : 'Nenhuma'}<br />
              <strong>Divisões:</strong> {transacao.divisoes ? JSON.stringify(transacao.divisoes) : 'Nenhuma'}
            </li>
          ))}
        </ul>
      ) : (
        <p>Nenhuma transação encontrada.</p>
      )}
    </div>
  );
};

export default PainelCentral;
