// src/pages/Home/Home.js
import React, { useEffect, useState } from 'react';
import { obterTransacoes, obterRelatorio } from '../../api';
import TransactionCard from '../../components/Transaction/TransactionCard';
import NovaTransacaoForm from '../../components/Transaction/NovaTransacaoForm';
import './Home.css';

const Home = () => {
  const [transacoes, setTransacoes] = useState([]);
  const [relatorio, setRelatorio] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);

  const carregarTransacoes = async () => {
    try {
      const dados = await obterTransacoes();
      const transacoesOrdenadas = (dados.transacoes || [])
        .sort((a, b) => new Date(b.data) - new Date(a.data))
        .slice(0, 10);
      setTransacoes(transacoesOrdenadas);
    } catch (error) {
      console.error('Erro ao carregar transações:', error);
    }
  };

  const carregarRelatorio = async () => {
    try {
      const dados = await obterRelatorio();
      setRelatorio(dados);
    } catch (error) {
      console.error('Erro ao carregar relatório:', error);
    }
  };

  useEffect(() => {
    carregarTransacoes();
    carregarRelatorio();
  }, []);

  return (
    <div className="home-container">
      <button className="new-transaction-btn" onClick={() => setModalOpen(true)}>
        Nova Transação
      </button>
      <div className="dashboard-summary">
        {relatorio ? (
          <>
            <h2>Resumo</h2>
            <p>Total Gastos: R${relatorio.totalGastos}</p>
            <p>Total Recebimentos: R${relatorio.totalRecebimentos}</p>
            <p>Saldo Geral: R${relatorio.saldoGeral}</p>
          </>
        ) : (
          <p>Carregando resumo...</p>
        )}
      </div>
      <div className="recent-transactions">
        <h2>Transações Recentes</h2>
        {transacoes.map(transacao => (
          <TransactionCard key={transacao.id} transacao={transacao} />
        ))}
      </div>
      {modalOpen && (
        <div className="modal-wrapper">
          <NovaTransacaoForm onClose={() => setModalOpen(false)} />
        </div>
      )}
    </div>
  );
};

export default Home;
