// src/components/Transaction/NovaTransacaoForm.js
import React, { useEffect, useState, useRef } from 'react';
import { criarTransacao, atualizarTransacao, obterCategorias, obterTags } from '../../api.js';
import './NovaTransacaoForm.css';

const NovaTransacaoForm = ({ onSuccess, onClose, transacao }) => {

  const setHoje = () => {
    const hoje = new Date().toISOString().split('T')[0];
    setData(hoje);
  };

  // Função que ajusta a data para ontem
  const setOntem = () => {
    const ontem = new Date(Date.now() - 86400000).toISOString().split('T')[0];
    setData(ontem);
  };
  
  // Estados para campos
  const [tipo, setTipo] = useState('gasto');
  const [descricao, setDescricao] = useState('');
  const [data, setData] = useState('');
  const [valorTotal, setValorTotal] = useState('');

  // Tags e categorias
  const [categorias, setCategorias] = useState([]);
  const [tags, setTags] = useState([]);
  const [globalTags, setGlobalTags] = useState({});

  // Pagamentos
  const [pagamentos, setPagamentos] = useState([
    { pessoa: '', valor: '', paymentTags: {} }
  ]);

  const descricaoSuggestions = ['Compra de pizza', 'Pagamento de conta', 'Arbitragem'];
  const pessoaSuggestions = ['Eu', 'Alisson', 'Emerson'];

  const descricaoRef = useRef(null);

  // useEffect para carregar dados do backend (categorias, tags)
  useEffect(() => {
    async function fetchData() {
      try {
        const cats = await obterCategorias();
        setCategorias(cats);

        const tgs = await obterTags();
        setTags(tgs);
      } catch (error) {
        console.error('Erro ao carregar categorias ou tags:', error);
      }
    }
    fetchData();
  }, []);

  // useEffect para “escutar” transacao e preencher os campos
  useEffect(() => {
    if (transacao) {
      setTipo(transacao.tipo);
      setDescricao(transacao.descricao);
      // Garantir que a data seja formatada (YYYY-MM-DD)
      const dataStr = transacao.data.split('T')[0];
      setData(dataStr);
      setValorTotal(String(transacao.valor));

      // Carregar tags globais
      if (transacao.tags) {
        setGlobalTags(transacao.tags);
      } else {
        setGlobalTags({});
      }

      // Carregar pagamentos
      if (transacao.pagamentos && transacao.pagamentos.length > 0) {
        setPagamentos(transacao.pagamentos);
      } else {
        setPagamentos([{ pessoa: '', valor: '', paymentTags: {} }]);
      }
    } else {
      // Nova transação: resetar os campos
      setTipo('gasto');
      setDescricao('');
      const hoje = new Date().toISOString().split('T')[0];
      setData(hoje);
      setValorTotal('');
      setGlobalTags({});
      setPagamentos([{ pessoa: '', valor: '', paymentTags: {} }]);
    }
  }, [transacao]);

  // Agrupa as tags por categoria
  const getTagsByCategory = () => {
    return tags.reduce((acc, tag) => {
      if (tag.categoria) {
        const catKey = tag.categoria;
        if (!acc[catKey]) {
          acc[catKey] = [];
        }
        acc[catKey].push(tag);
      }
      return acc;
    }, {});
  };
  const tagsPorCategoria = getTagsByCategory();

  const handleGlobalTagChange = (categoria, selectedOptions) => {
    setGlobalTags(prev => ({
      ...prev,
      [categoria]: selectedOptions
    }));
  };

  // Pagamentos
  const handlePagamentoChange = (index, field, value) => {
    const novos = [...pagamentos];
    novos[index][field] = value;
    setPagamentos(novos);
  };

  const handlePagamentoTagChange = (index, categoria, selectedOptions) => {
    const novos = [...pagamentos];
    if (!novos[index].paymentTags) {
      novos[index].paymentTags = {};
    }
    novos[index].paymentTags[categoria] = selectedOptions;
    setPagamentos(novos);
  };

  const addPagamento = () => {
    setPagamentos([...pagamentos, { pessoa: '', valor: '', paymentTags: {} }]);
  };

  const removePagamento = (index) => {
    const novos = pagamentos.filter((_, i) => i !== index);
    setPagamentos(novos);
  };

  // Validação de soma
  const validatePagamentos = () => {
    const soma = pagamentos.reduce((acc, pag) => {
      const v = parseFloat(pag.valor || 0);
      return acc + (isNaN(v) ? 0 : v);
    }, 0);
    return parseFloat(valorTotal || 0) === soma;
  };

  // Submit
  const handleSubmit = async (e, closeModal = true) => {
    e.preventDefault();
    if (!validatePagamentos()) {
      alert('A soma dos pagamentos deve ser igual ao valor total.');
      return;
    }
    const novaTransacao = {
      tipo,
      descricao,
      data,
      valor: Number(parseFloat(valorTotal).toFixed(2)),
      tags: globalTags,
      pagamentos: pagamentos.map(pag => ({
        pessoa: pag.pessoa,
        valor: Number(parseFloat(pag.valor).toFixed(2)),
        tags: pag.paymentTags
      }))
    };
    try {
      if (transacao && transacao.id) {
        await atualizarTransacao(transacao.id, novaTransacao);
        alert('Transação atualizada com sucesso!');
      } else {
        await criarTransacao(novaTransacao);
        alert('Transação criada com sucesso!');
      }
      if (onSuccess) onSuccess();
      if (closeModal) {
        onClose();
      } else {
        // Limpa para nova inserção
        setTipo('gasto');
        setDescricao('');
        const hoje = new Date().toISOString().split('T')[0];
        setData(hoje);
        setValorTotal('');
        setGlobalTags({});
        setPagamentos([{ pessoa: '', valor: '', paymentTags: {} }]);
        descricaoRef.current && descricaoRef.current.focus();
      }
    } catch (error) {
      console.error('Erro ao criar/atualizar transação:', error);
      alert('Erro ao criar/atualizar transação.');
    }
  };

  // Render
  return (
    <div className="nova-transacao-form-container">
      <h2>{transacao ? 'Editar Transação' : 'Nova Transação'}</h2>
      <form onSubmit={(e) => handleSubmit(e, true)}>
        <div className="form-grid">
          <div className="left-column">
            <div className="form-section">
              <label>Tipo:</label>
              <select
                value={tipo}
                onChange={e => setTipo(e.target.value)}
                required
              >
                <option value="gasto">Gasto</option>
                <option value="recebivel">Recebível</option>
              </select>
            </div>
            <div className="form-section">
              <label>Descrição:</label>
              <input
                type="text"
                value={descricao}
                onChange={e => setDescricao(e.target.value)}
                required
                list="descricao-suggestions"
                ref={descricaoRef}
              />
              <datalist id="descricao-suggestions">
                {descricaoSuggestions.map((sug, idx) => (
                  <option key={idx} value={sug} />
                ))}
              </datalist>
            </div>
            <div className="form-section">
              <label>Data:</label>
              <input
                type="date"
                value={data}
                onChange={e => setData(e.target.value)}
                required
              />
              <div className="date-shortcuts">
                <button type="button" onClick={setHoje}>Hoje</button>
                <button type="button" onClick={setOntem}>Ontem</button>
              </div>
            </div>
            <div className="form-section">
              <label>Valor Total:</label>
              <input
                type="number"
                step="0.01"
                value={valorTotal}
                onChange={e => setValorTotal(e.target.value)}
                required
              />
            </div>
          </div>

          <div className="right-column">
            <div className="form-section global-tags">
              <h3>Tags Globais</h3>
              {categorias.map(cat => (
                <div key={cat.id} className="tag-dropdown-group">
                  <label>{cat.nome}:</label>
                  <select
                    multiple
                    value={globalTags[cat.nome] || []}
                    onChange={e => {
                      const selected = Array.from(e.target.selectedOptions, opt => opt.value);
                      handleGlobalTagChange(cat.nome, selected);
                    }}
                  >
                    {(tagsPorCategoria[cat.nome] || []).map(tag => (
                      <option key={tag.id} value={tag.nome}>
                        {tag.nome}
                      </option>
                    ))}
                  </select>
                </div>
              ))}
            </div>

            <div className="form-section pagamentos-section">
              <h3>Pagamentos</h3>
              {pagamentos.map((pagamento, index) => (
                <div key={index} className="pagamento-item">
                  <div className="form-section">
                    <label>Pessoa:</label>
                    <input
                      type="text"
                      value={pagamento.pessoa}
                      onChange={e => handlePagamentoChange(index, 'pessoa', e.target.value)}
                      required
                      list="pessoa-suggestions"
                    />
                    <datalist id="pessoa-suggestions">
                      {pessoaSuggestions.map((sug, idx) => (
                        <option key={idx} value={sug} />
                      ))}
                    </datalist>
                  </div>
                  <div className="form-section">
                    <label>Valor:</label>
                    <input
                      type="number"
                      step="0.01"
                      value={pagamento.valor}
                      onChange={e => handlePagamentoChange(index, 'valor', e.target.value)}
                      required
                    />
                  </div>
                  <div className="form-section payment-tags">
                    <h4>Tags para Pagamento</h4>
                    {categorias.map(cat => (
                      <div key={cat.id} className="tag-dropdown-group">
                        <label>{cat.nome}:</label>
                        <select
                          multiple
                          value={(pagamento.paymentTags && pagamento.paymentTags[cat.nome]) || []}
                          onChange={e => {
                            const selected = Array.from(e.target.selectedOptions, opt => opt.value);
                            handlePagamentoTagChange(index, cat.nome, selected);
                          }}
                        >
                          {(tagsPorCategoria[cat.nome] || []).map(tag => (
                            <option key={tag.id} value={tag.nome}>
                              {tag.nome}
                            </option>
                          ))}
                        </select>
                      </div>
                    ))}
                  </div>
                  <button type="button" onClick={() => removePagamento(index)}>
                    Remover
                  </button>
                  <hr />
                </div>
              ))}
              <button type="button" onClick={addPagamento}>Adicionar Pagamento</button>
            </div>
          </div>
        </div>

        <div className="form-buttons">
          <button
            type="submit"
            className="submit-btn"
            onClick={(e) => handleSubmit(e, true)}
          >
            {transacao ? 'Atualizar e Fechar' : 'Salvar e Fechar'}
          </button>
          <button
            type="button"
            className="submit-btn"
            onClick={(e) => handleSubmit(e, false)}
          >
            {transacao ? 'Atualizar e Continuar' : 'Salvar e Continuar'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default NovaTransacaoForm;
