// src/pages/Relatorio/Relatorio.js
import React, { useEffect, useState } from 'react';
import { obterTransacoes, obterCategorias, obterTags } from '../../api.js';
import './Relatorio.css';

const Relatorio = () => {
  const [transacoes, setTransacoes] = useState([]);
  const [filteredTransacoes, setFilteredTransacoes] = useState([]);

  // Estados dos filtros
  const [dataInicio, setDataInicio] = useState('');
  const [dataFim, setDataFim] = useState('');
  const [selectedPessoas, setSelectedPessoas] = useState([]);
  const [categorias, setCategorias] = useState([]);
  const [tags, setTags] = useState([]);
  // tagFilters: objeto onde a chave é o nome da categoria e o valor é um array de tags selecionadas
  const [tagFilters, setTagFilters] = useState({});

  useEffect(() => {
    async function fetchData() {
      try {
        const transData = await obterTransacoes();
        const transArray = transData.transacoes || [];
        setTransacoes(transArray);
        setFilteredTransacoes(transArray);
        const cats = await obterCategorias();
        setCategorias(cats);
        const initTagFilters = {};
        cats.forEach(cat => {
          initTagFilters[cat.nome] = [];
        });
        setTagFilters(initTagFilters);
        const tgs = await obterTags();
        setTags(tgs);
      } catch (error) {
        console.error('Erro ao carregar dados para relatório:', error);
      }
    }
    fetchData();
  }, []);

  // Extrai uma lista de pessoas distintas dos pagamentos
  const distinctPessoas = () => {
    const pessoasSet = new Set();
    transacoes.forEach(tr => {
      if(tr.pagamentos) {
        tr.pagamentos.forEach(p => {
          if(p.pessoa) pessoasSet.add(p.pessoa);
        });
      }
    });
    return Array.from(pessoasSet);
  };

  // Agrupa as tags por categoria a partir da lista de tags
  const tagsPorCategoria = tags.reduce((acc, tag) => {
    if(tag.categoria) {
      if(!acc[tag.categoria]) {
        acc[tag.categoria] = [];
      }
      acc[tag.categoria].push(tag);
    }
    return acc;
  }, {});

  // Aplica os filtros
  const applyFilters = () => {
    let result = transacoes;
    // Filtro por data
    if(dataInicio) {
      result = result.filter(tr => new Date(tr.data) >= new Date(dataInicio));
    }
    if(dataFim) {
      result = result.filter(tr => new Date(tr.data) <= new Date(dataFim));
    }
    // Filtro por pessoas
    if(selectedPessoas.length > 0) {
      result = result.filter(tr => tr.pagamentos && tr.pagamentos.some(p => selectedPessoas.includes(p.pessoa)));
    }
    // Filtro por tags: considerar tanto as tags do elemento pai quanto as tags dos pagamentos
    Object.keys(tagFilters).forEach(cat => {
      const selectedTags = tagFilters[cat];
      if(selectedTags && selectedTags.length > 0) {
        result = result.filter(tr => {
          const parentMatch =
            tr.tags &&
            tr.tags[cat] &&
            tr.tags[cat].some(tag =>
              selectedTags.map(t => t.toLowerCase()).includes(tag.toLowerCase())
            );
          const paymentsMatch =
            tr.pagamentos &&
            tr.pagamentos.some(p =>
              p.tags &&
              p.tags[cat] &&
              p.tags[cat].some(tag =>
                selectedTags.map(t => t.toLowerCase()).includes(tag.toLowerCase())
              )
            );
          return parentMatch || paymentsMatch;
        });
      }
    });
    setFilteredTransacoes(result);
  };

  return (
    <div className="relatorio-container">
      <h2 className="relatorio-title">Relatórios</h2>
      <div className="filter-panel">
        <div className="filter-group">
          <label>Data Início:</label>
          <input type="date" value={dataInicio} onChange={e => setDataInicio(e.target.value)} />
        </div>
        <div className="filter-group">
          <label>Data Fim:</label>
          <input type="date" value={dataFim} onChange={e => setDataFim(e.target.value)} />
        </div>
        <div className="filter-group">
          <label>Pessoas:</label>
          <select multiple value={selectedPessoas}
            onChange={e => {
              const values = Array.from(e.target.selectedOptions, option => option.value);
              setSelectedPessoas(values);
            }}>
            {distinctPessoas().map((p, idx) => (
              <option key={idx} value={p}>{p}</option>
            ))}
          </select>
        </div>
        {categorias.map(cat => (
          <div key={cat.id} className="filter-group">
            <label>{cat.nome} (Tags):</label>
            <select multiple
              value={tagFilters[cat.nome] || []}
              onChange={e => {
                const selected = Array.from(e.target.selectedOptions, option => option.value);
                setTagFilters(prev => ({ ...prev, [cat.nome]: selected }));
              }}>
              {(tagsPorCategoria[cat.nome] || []).map(tag => (
                <option key={tag.id} value={tag.nome}>{tag.nome}</option>
              ))}
            </select>
          </div>
        ))}
        <div className="filter-group">
          <button onClick={applyFilters}>Filtrar</button>
        </div>
      </div>
      <div className="relatorio-results">
        <h3>Resultados ({filteredTransacoes.length})</h3>
        {filteredTransacoes.length > 0 ? (
          <table className="relatorio-table">
            <thead>
              <tr>
                <th>Descrição</th>
                <th>Data</th>
                <th>Valor</th>
                <th>Tipo</th>
                <th>Pessoas</th>
                <th>Tags</th>
              </tr>
            </thead>
            <tbody>
              {filteredTransacoes.map((tr, idx) => (
                <tr key={idx}>
                  <td>{tr.descricao}</td>
                  <td>{new Date(tr.data).toLocaleDateString('pt-BR', { timeZone: 'UTC' })}</td>
                  <td>R${tr.valor}</td>
                  <td>{tr.tipo}</td>
                  <td>
                    {tr.pagamentos && tr.pagamentos.map((pag, i) => (
                      <div key={i}>{pag.pessoa}</div>
                    ))}
                  </td>
                  <td>
                    {tr.tags && Object.keys(tr.tags).map((cat, i) => (
                      <div key={i}><strong>{cat}:</strong> {tr.tags[cat].join(', ')}</div>
                    ))}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>Nenhum registro encontrado.</p>
        )}
      </div>
    </div>
  );
};

export default Relatorio;
