import React from 'react';
import { FaArrowUp, FaArrowDown, FaEdit, FaTrash } from 'react-icons/fa';
import './TransactionCard.css';

const TransactionCard = ({ transacao, onEdit, onDelete }) => {
  // Determina o ícone com base no tipo da transação
  const tipoIcon =
    transacao.tipo === 'gasto' ? (
      <FaArrowDown className="tipo-icon gasto" />
    ) : (
      <FaArrowUp className="tipo-icon recebivel" />
    );

  return (
    <div className="transaction-card">
      <div className="card-header">
        <h3 className="card-title">{transacao.descricao}</h3>
        <div className="card-header-icon">
          {tipoIcon}
          {/* Opcional: adicionar outro ícone para status/categoria */}
        </div>
      </div>
      <hr className="divider" />
      <div className="card-info">
        <div className="info-date">
          {new Date(transacao.data).toLocaleDateString('pt-BR')}
        </div>
        <div className="info-value">
          R${parseFloat(transacao.valor).toFixed(2)}
          <span className="badge">{transacao.tipo === 'gasto' ? 'Gasto' : 'Recebível'}</span>
        </div>
      </div>
      <div className="card-details">
        <div className="payment-info">
          {transacao.pagamentos && transacao.pagamentos.length > 0 ? (
            transacao.pagamentos.map((pag, idx) => (
              <div key={idx} className="payment-row">
                <span className="payment-person">{pag.pessoa}</span>:
                <span className="payment-amount"> R${parseFloat(pag.valor).toFixed(2)}</span>
              </div>
            ))
          ) : (
            <div className="no-payments">Sem pagamentos</div>
          )}
        </div>
        <div className="tag-info">
          {transacao.tags &&
            Object.keys(transacao.tags).map((cat, idx) =>
              transacao.tags[cat].map((tag, jdx) => (
                <span key={`${idx}-${jdx}`} className="tag-chip">
                  {cat}: {tag}
                </span>
              ))
            )}
        </div>
      </div>
      <div className="card-actions">
        <button className="action-btn" onClick={() => onEdit(transacao)}>
          <FaEdit />
        </button>
        <button className="action-btn" onClick={() => onDelete(transacao.id)}>
          <FaTrash />
        </button>
      </div>
    </div>
  );
};

export default TransactionCard;
