// src/App.js
import React, { useState } from 'react';
import LayoutPrincipal from './components/Layout/LayoutPrincipal';
import TagManagement from './components/Tag/TagManagement';
import './App.css';

function App() {
  const [mostrarTagManagement, setMostrarTagManagement] = useState(false);

  return (
    <div className="App">
      <header className="app-header">
        <div className="header-left">
          {/* Espa√ßo para logo, se desejar */}
          {/* <img src="/logo.svg" alt="Logo Controle de Gastos" className="logo" /> */}
          <h1>Controle de Gastos</h1>
        </div>
        <button className="btn-gerenciar-tags" onClick={() => setMostrarTagManagement(true)}>
          Gerenciar Tags
        </button>
      </header>
      <LayoutPrincipal />
      {mostrarTagManagement && (
        <TagManagement onClose={() => setMostrarTagManagement(false)} />
      )}
    </div>
  );
}

export default App;
