import React, { useState } from 'react';
import ModalTransacao from '../Modal/ModalTransacao';
import NovaTransacaoForm from './NovaTransacaoForm';

const PainelEsquerdo = () => {
  const [modalAberto, setModalAberto] = useState(false);

  return (
    <div>
      <h2>Lançamento</h2>
      <button onClick={() => setModalAberto(true)}>Nova Transação</button>
      {modalAberto && (
        <ModalTransacao onClose={() => setModalAberto(false)}>
          <NovaTransacaoForm onSuccess={() => {}} onClose={() => setModalAberto(false)} />
        </ModalTransacao>
      )}
    </div>
  );
};

export default PainelEsquerdo;
