// src/pages/Transacoes/Transacoes.js
import React, { useEffect, useState } from 'react';
import { obterTransacoes, excluirTransacao } from '../../api';
import TransactionCard from '../../components/Transaction/TransactionCard';
import NovaTransacaoForm from '../../components/Transaction/NovaTransacaoForm';
import ModalTransacao from '../../components/Modal/ModalTransacao';
import './Transacoes.css';

const Transacoes = () => {
  const [transacoes, setTransacoes] = useState([]);
  const [editingTransacao, setEditingTransacao] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);

  const carregarTransacoes = async () => {
    try {
      const dados = await obterTransacoes();
      const transacoesOrdenadas = (dados.transacoes || [])
        .sort((a, b) => new Date(b.data) - new Date(a.data));
      setTransacoes(transacoesOrdenadas);
    } catch (error) {
      console.error('Erro ao carregar transações:', error);
    }
  };

  useEffect(() => {
    carregarTransacoes();
  }, []);

  const handleEdit = (transacao) => {
    setEditingTransacao(transacao);
    setModalOpen(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Tem certeza que deseja excluir esta transação?')) {
      try {
        await excluirTransacao(id);
        alert('Transação excluída com sucesso!');
        carregarTransacoes();
      } catch (error) {
        console.error('Erro ao excluir transação:', error);
        alert('Erro ao excluir transação.');
      }
    }
  };

  const closeModal = () => {
    setEditingTransacao(null);
    setModalOpen(false);
  };

  return (
    <div className="transacoes-page">
      <h2>Transações</h2>
      {transacoes.length > 0 ? (
        <div className="transacoes-list">
          {transacoes.map(transacao => (
            <div key={transacao.id} className="transaction-card-wrapper">
              <TransactionCard transacao={transacao} />
              <div className="transaction-actions">
                <button onClick={() => handleEdit(transacao)}>Editar</button>
                <button onClick={() => handleDelete(transacao.id)}>Excluir</button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>Nenhuma transação encontrada.</p>
      )}

      {modalOpen && (
        <ModalTransacao onClose={closeModal}>
          <NovaTransacaoForm
            transacao={editingTransacao}
            onSuccess={() => {
              closeModal();
              carregarTransacoes();
            }}
            onClose={closeModal}
          />
        </ModalTransacao>
      )}
    </div>
  );
};

export default Transacoes;
