// src/components/TagManagement.js

import React, { useEffect, useState } from 'react';
import { obterTags, criarTag, atualizarTag, excluirTag } from '../../api.js';
import './ModalTagManagement.css';

const TagManagement = ({ onClose }) => {
  const [tags, setTags] = useState([]);
  const [novoNome, setNovoNome] = useState('');
  const [novaDescricao, setNovaDescricao] = useState('');
  const [editandoTagId, setEditandoTagId] = useState(null);
  const [editNome, setEditNome] = useState('');
  const [editDescricao, setEditDescricao] = useState('');

  const carregarTags = async () => {
    try {
      const dados = await obterTags();
      setTags(dados);
    } catch (error) {
      console.error('Erro ao carregar tags:', error);
    }
  };

  useEffect(() => {
    carregarTags();
  }, []);

  const handleAdicionarTag = async () => {
    if (!novoNome) {
      alert('O nome da tag é obrigatório.');
      return;
    }
    try {
      await criarTag({ nome: novoNome, descricao: novaDescricao });
      setNovoNome('');
      setNovaDescricao('');
      carregarTags();
    } catch (error) {
      console.error('Erro ao criar tag:', error);
    }
  };

  const handleEditarTag = (tag) => {
    setEditandoTagId(tag.id);
    setEditNome(tag.nome);
    setEditDescricao(tag.descricao);
  };

  const handleSalvarEdicao = async () => {
    if (!editNome) {
      alert('O nome da tag é obrigatório.');
      return;
    }
    try {
      await atualizarTag(editandoTagId, { nome: editNome, descricao: editDescricao });
      setEditandoTagId(null);
      setEditNome('');
      setEditDescricao('');
      carregarTags();
    } catch (error) {
      console.error('Erro ao atualizar tag:', error);
    }
  };

  const handleExcluirTag = async (id) => {
    if (window.confirm('Tem certeza que deseja excluir essa tag?')) {
      try {
        await excluirTag(id);
        carregarTags();
      } catch (error) {
        console.error('Erro ao excluir tag:', error);
      }
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="modal-close" onClick={onClose}>X</button>
        <h2>Gerenciar Tags</h2>
        <div className="tag-list">
          {tags.length > 0 ? (
            <ul>
              {tags.map(tag => (
                <li key={tag.id}>
                  {editandoTagId === tag.id ? (
                    <div>
                      <input
                        type="text"
                        value={editNome}
                        onChange={(e) => setEditNome(e.target.value)}
                        placeholder="Nome da tag"
                      />
                      <input
                        type="text"
                        value={editDescricao}
                        onChange={(e) => setEditDescricao(e.target.value)}
                        placeholder="Descrição (opcional)"
                      />
                      <button onClick={handleSalvarEdicao}>Salvar</button>
                      <button onClick={() => setEditandoTagId(null)}>Cancelar</button>
                    </div>
                  ) : (
                    <div>
                      <strong>{tag.nome}</strong>{tag.descricao && ` - ${tag.descricao}`}
                      <button onClick={() => handleEditarTag(tag)}>Editar</button>
                      <button onClick={() => handleExcluirTag(tag.id)}>Excluir</button>
                    </div>
                  )}
                </li>
              ))}
            </ul>
          ) : (
            <p>Nenhuma tag encontrada.</p>
          )}
        </div>
        <div className="nova-tag">
          <h3>Adicionar Nova Tag</h3>
          <input
            type="text"
            value={novoNome}
            onChange={(e) => setNovoNome(e.target.value)}
            placeholder="Nome da tag"
          />
          <input
            type="text"
            value={novaDescricao}
            onChange={(e) => setNovaDescricao(e.target.value)}
            placeholder="Descrição (opcional)"
          />
          <button onClick={handleAdicionarTag}>Adicionar</button>
        </div>
      </div>
    </div>
  );
};

export default TagManagement;
