// src/pages/Relatorio/Relatorio.js
import React, { useEffect, useState } from 'react';
import { obterTransacoes, obterCategorias, obterTags } from '../../api';
import './Relatorio.css';

const Relatorio = () => {
  const [transacoes, setTransacoes] = useState([]);
  const [filteredTransacoes, setFilteredTransacoes] = useState([]);
  
  // Filtros
  const [dataInicio, setDataInicio] = useState('');
  const [dataFim, setDataFim] = useState('');
  const [selectedTipo, setSelectedTipo] = useState('both');
  const [selectedPessoas, setSelectedPessoas] = useState([]);
  
  // Indicadores analíticos
  const [summaryInfo, setSummaryInfo] = useState({
    totalRecebimentos: '0.00',
    totalGastos: '0.00',
    saldoLiquido: '0.00',
    totalPagamentos: 0,
    mediaPagamento: '0.00',
    mediaPorPessoa: '0.00'
  });
  
  // Carrega transações do backend
  const carregarTransacoes = async () => {
    try {
      const dados = await obterTransacoes();
      // Ordena as transações por data (descendente)
      const lista = (dados.transacoes || []).sort(
        (a, b) => new Date(b.data) - new Date(a.data)
      );
      setTransacoes(lista);
      setFilteredTransacoes(lista);
    } catch (error) {
      console.error('Erro ao carregar transações:', error);
    }
  };
  
  useEffect(() => {
    carregarTransacoes();
  }, []);
  
  // Filtro por data, tipo e pessoa
  useEffect(() => {
    let resultado = [...transacoes];
    if (dataInicio) {
      resultado = resultado.filter(tr => new Date(tr.data) >= new Date(dataInicio));
    }
    if (dataFim) {
      resultado = resultado.filter(tr => new Date(tr.data) <= new Date(dataFim));
    }
    if (selectedTipo !== 'both') {
      resultado = resultado.filter(tr => tr.tipo === selectedTipo);
    }
    if (selectedPessoas.length > 0) {
      resultado = resultado.filter(tr =>
        tr.pagamentos &&
        tr.pagamentos.some(p => selectedPessoas.includes(p.pessoa))
      );
    }
    setFilteredTransacoes(resultado);
  }, [dataInicio, dataFim, selectedTipo, selectedPessoas, transacoes]);
  
  // Flatten pagamentos e calcular indicadores
  useEffect(() => {
    // Para cada transação, extraímos os pagamentos,
    // propagando o "tipo", "data" e "descrição" da transação para cada pagamento.
    const allPayments = [];
    filteredTransacoes.forEach(tr => {
      if (tr.pagamentos && tr.pagamentos.length > 0) {
        tr.pagamentos.forEach(p => {
          allPayments.push({
            data: tr.data,
            descricao: tr.descricao,
            tipo: tr.tipo, // "gasto" ou "recebivel"
            pessoa: p.pessoa,
            valor: parseFloat(p.valor || 0),
            tags: p.tags || {}
          });
        });
      }
    });
    
    const totalPagamentos = allPayments.length;
    const totalRecebimentos = allPayments
      .filter(p => p.tipo === 'recebivel')
      .reduce((acc, p) => acc + p.valor, 0);
    const totalGastos = allPayments
      .filter(p => p.tipo === 'gasto')
      .reduce((acc, p) => acc + p.valor, 0);
    const saldoLiquido = totalRecebimentos - totalGastos;
    const mediaPagamento = totalPagamentos > 0 ? (allPayments.reduce((acc, p) => acc + p.valor, 0) / totalPagamentos) : 0;
    
    // Média por pessoa: agrupar por pessoa
    const pessoaMap = {};
    allPayments.forEach(p => {
      if (p.pessoa) {
        if (!pessoaMap[p.pessoa]) {
          pessoaMap[p.pessoa] = { total: 0, count: 0 };
        }
        pessoaMap[p.pessoa].total += p.valor;
        pessoaMap[p.pessoa].count += 1;
      }
    });
    const totalPessoas = Object.keys(pessoaMap).length;
    const mediaPorPessoa = totalPessoas > 0 
      ? (Object.values(pessoaMap).reduce((acc, obj) => acc + (obj.total / obj.count), 0) / totalPessoas)
      : 0;
    
    setSummaryInfo({
      totalRecebimentos: totalRecebimentos.toFixed(2),
      totalGastos: totalGastos.toFixed(2),
      saldoLiquido: saldoLiquido.toFixed(2),
      totalPagamentos,
      mediaPagamento: mediaPagamento.toFixed(2),
      mediaPorPessoa: mediaPorPessoa.toFixed(2)
    });
  }, [filteredTransacoes]);
  
  // Funções de filtros adicionais (para pessoas) – extrair pessoas únicas
  const distinctPessoas = () => {
    const pessoasSet = new Set();
    filteredTransacoes.forEach(tr => {
      if (tr.pagamentos) {
        tr.pagamentos.forEach(p => {
          if (p.pessoa) pessoasSet.add(p.pessoa);
        });
      }
    });
    return Array.from(pessoasSet);
  };
  
  // Handlers para edição, criação, exclusão
  const handleEdit = (transacao) => {
    // Abre modal em modo edição
    setEditingTransacao(transacao);
    setModalOpen(true);
  };
  
  const handleCreate = () => {
    setEditingTransacao(null);
    setModalOpen(true);
  };
  
  const handleCloseModal = () => {
    setEditingTransacao(null);
    setModalOpen(false);
  };
  
  const handleSuccess = () => {
    handleCloseModal();
    carregarTransacoes();
  };
  
  const handleDelete = async (id) => {
    if (window.confirm('Tem certeza que deseja excluir esta transação?')) {
      try {
        await excluirTransacao(id);
        alert('Transação excluída com sucesso!');
        carregarTransacoes();
      } catch (error) {
        console.error('Erro ao excluir transação:', error);
        alert('Erro ao excluir transação.');
      }
    }
  };
  
  return (
    <div className="relatorio-container">
      <h2 className="relatorio-title">Relatórios</h2>
      
      {/* Filtros */}
      <div className="filter-panel">
        <div className="filter-group">
          <label>Data Início:</label>
          <input type="date" value={dataInicio} onChange={e => setDataInicio(e.target.value)} />
        </div>
        <div className="filter-group">
          <label>Data Fim:</label>
          <input type="date" value={dataFim} onChange={e => setDataFim(e.target.value)} />
        </div>
        <div className="filter-group">
          <label>Tipo:</label>
          <select value={selectedTipo} onChange={e => setSelectedTipo(e.target.value)}>
            <option value="todos">Todos</option>
            <option value="gasto">Gasto</option>
            <option value="recebivel">Recebível</option>
          </select>
        </div>
        <div className="filter-group">
          <label>Pessoa:</label>
          <select
            multiple
            value={selectedPessoas}
            onChange={e => {
              const values = Array.from(e.target.selectedOptions, opt => opt.value);
              setSelectedPessoas(values);
            }}
          >
            {distinctPessoas().map((p, idx) => (
              <option key={idx} value={p}>{p}</option>
            ))}
          </select>
        </div>
        <button onClick={ () => { /* Caso queira disparar filtros manualmente; se os filtros já atualizam automaticamente, pode remover */ } }>
          Aplicar Filtros
        </button>
      </div>
      
      {/* Painel de Resumo (Dashboard Cards) */}
      <div className="dashboard-cards">
        <div className="card">
          <h3>Total Recebimentos</h3>
          <p className="card-value">R$ {summaryInfo.totalRecebimentos}</p>
        </div>
        <div className="card">
          <h3>Total Gastos</h3>
          <p className="card-value">R$ {summaryInfo.totalGastos}</p>
        </div>
        <div className="card">
          <h3>Saldo Líquido</h3>
          <p className="card-value">R$ {summaryInfo.saldoLiquido}</p>
        </div>
        <div className="card">
          <h3>Nº de Pagamentos</h3>
          <p className="card-value">{summaryInfo.totalPagamentos}</p>
        </div>
        <div className="card">
          <h3>Média por Pagamento</h3>
          <p className="card-value">R$ {summaryInfo.mediaPagamento}</p>
        </div>
        <div className="card">
          <h3>Média por Pessoa</h3>
          <p className="card-value">R$ {summaryInfo.mediaPorPessoa}</p>
        </div>
      </div>
      
      {/* Tabela Detalhada */}
      <div className="relatorio-results">
        <h3>Resultados ({filteredTransacoes.length})</h3>
        {filteredTransacoes.length > 0 ? (
          <table className="relatorio-table">
            <thead>
              <tr>
                <th>Data</th>
                <th>Descrição</th>
                <th>Pessoa(s)</th>
                <th>Tipo</th>
                <th>Valor</th>
              </tr>
            </thead>
            <tbody>
              {filteredTransacoes.map((tr, idx) => {
                // Agrupa as pessoas dos pagamentos e soma os valores
                const pessoas = tr.pagamentos.map(p => p.pessoa).join(', ');
                const totalPagamento = tr.pagamentos.reduce((acc, p) => acc + parseFloat(p.valor || 0), 0);
                return (
                  <tr key={idx}>
                    <td>{new Date(tr.data).toLocaleDateString('pt-BR')}</td>
                    <td>{tr.descricao}</td>
                    <td>{pessoas}</td>
                    <td>{tr.tipo}</td>
                    <td>R$ {totalPagamento.toFixed(2)}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        ) : (
          <p>Nenhum registro encontrado.</p>
        )}
      </div>
      
      {/* Modal para criar/editar transação */}
      {/*
          O ModalTransacao deve funcionar para edição/ criação.
      */}
      {/*
      Se o modal estiver aberto, renderize o formulário.
      */}
      {modalOpen && (
        <ModalTransacao onClose={handleCloseModal}>
          <NovaTransacaoForm
            transacao={editingTransacao}
            onSuccess={handleSuccess}
            onClose={handleCloseModal}
          />
        </ModalTransacao>
      )}
    </div>
  );
};

export default Relatorio;
