// src/components/PainelEsquerdo.js

import React, { useState } from 'react';
import { criarTransacao } from '../api.js';
import ModalTransacao from '../components/Modal/ModalTransacao.js';

const PainelEsquerdo = () => {
  const [modalAberto, setModalAberto] = useState(false);
  const [tipo, setTipo] = useState('gasto');
  const [valor, setValor] = useState('');
  const [data, setData] = useState('');
  const [pessoa, setPessoa] = useState('');
  const [tags, setTags] = useState('');
  const [divisoes, setDivisoes] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const tagsArray = tags ? tags.split(',').map(tag => tag.trim()) : [];
    let divisoesArray = [];
    if (divisoes) {
      try {
        divisoesArray = JSON.parse(divisoes);
      } catch (error) {
        alert('Formato inválido para divisões. Verifique o JSON.');
        return;
      }
    }
    const novaTransacao = {
      tipo,
      valor: parseFloat(valor),
      data,
      pessoa,
      tags: tagsArray,
      divisoes: divisoesArray
    };
    try {
      await criarTransacao(novaTransacao);
      alert('Transação criada com sucesso!');
      // Limpa os campos
      setTipo('gasto');
      setValor('');
      setData('');
      setPessoa('');
      setTags('');
      setDivisoes('');
      setModalAberto(false);
    } catch (error) {
      console.error('Erro ao criar transação:', error);
      alert('Erro ao criar transação.');
    }
  };

  return (
    <div>
      <h2>Lançamento</h2>
      <button onClick={() => setModalAberto(true)}>Nova Transação</button>
      {modalAberto && (
        <ModalTransacao onClose={() => setModalAberto(false)}>
          <h2>Nova Transação</h2>
          <form onSubmit={handleSubmit}>
            <div>
              <label>Tipo:</label>
              <select value={tipo} onChange={(e) => setTipo(e.target.value)}>
                <option value="gasto">Gasto</option>
                <option value="recebimento">Recebimento</option>
              </select>
            </div>
            <div>
              <label>Valor:</label>
              <input type="number" step="0.01" value={valor} onChange={(e) => setValor(e.target.value)} required />
            </div>
            <div>
              <label>Data:</label>
              <input type="date" value={data} onChange={(e) => setData(e.target.value)} required />
            </div>
            <div>
              <label>Pessoa:</label>
              <input type="text" value={pessoa} onChange={(e) => setPessoa(e.target.value)} required />
            </div>
            <div>
              <label>Tags (separadas por vírgula):</label>
              <input type="text" value={tags} onChange={(e) => setTags(e.target.value)} />
            </div>
            <div>
              <label>Divisões (formato JSON):</label>
              <textarea
                value={divisoes}
                onChange={(e) => setDivisoes(e.target.value)}
                placeholder='Ex: [{"pessoa": "Maria", "valor": 40}]'
              />
            </div>
            <button type="submit">Salvar Transação</button>
          </form>
        </ModalTransacao>
      )}
    </div>
  );
};

export default PainelEsquerdo;
