// src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import LayoutPrincipal from './components/Layout/LayoutPrincipal';
import Relatorio from './components/Relatorio/Relatorio';
import TagManagement from './components/Tag/TagManagement';
import './App.css';

function App() {
  const [mostrarTagManagement, setMostrarTagManagement] = useState(false);

  return (
    <Router>
      <div className="App">
        <header className="app-header">
          <div className="header-left">
            <h1>Controle de Gastos</h1>
          </div>
          <nav className="navigation">
            <Link to="/" className="nav-link">Home</Link>
            <Link to="/relatorio" className="nav-link">Relat√≥rios</Link>
            <button 
              className="nav-button" 
              onClick={() => setMostrarTagManagement(true)}
            >
              Gerenciar Tags
            </button>
          </nav>
        </header>
        <Routes>
          <Route path="/" element={<LayoutPrincipal />} />
          <Route path="/relatorio" element={<Relatorio />} />
        </Routes>
        {mostrarTagManagement && (
          <TagManagement onClose={() => setMostrarTagManagement(false)} />
        )}
      </div>
    </Router>
  );
}

export default App;
