// src/components/LayoutPrincipal.js

import React, { useState } from 'react';
import PainelEsquerdo from './PainelEsquerdo';
import PainelCentral from './PainelCentral';
import PainelDireito from './PainelDireito';

const LayoutPrincipal = () => {
  // Estados para controlar se os painéis laterais estão visíveis
  const [mostrarEsquerdo, setMostrarEsquerdo] = useState(true);
  const [mostrarDireito, setMostrarDireito] = useState(true);

  const toggleEsquerdo = () => {
    setMostrarEsquerdo(prev => !prev);
  };

  const toggleDireito = () => {
    setMostrarDireito(prev => !prev);
  };

  return (
    <div className="layout-container">
      {/* Painel Esquerdo */}
      <div className={`painel painel-esquerdo ${!mostrarEsquerdo ? 'oculto' : ''}`}>
        <button className="botao-toggle" onClick={toggleEsquerdo}>
          {mostrarEsquerdo ? 'Ocultar Lançamento' : 'Mostrar Lançamento'}
        </button>
        {mostrarEsquerdo && <PainelEsquerdo />}
      </div>

      {/* Painel Central */}
      <div className="painel painel-central">
        <PainelCentral />
      </div>

      {/* Painel Direito */}
      <div className={`painel painel-direito ${!mostrarDireito ? 'oculto-direita' : ''}`}>
        <button className="botao-toggle" onClick={toggleDireito}>
          {mostrarDireito ? 'Ocultar Filtros' : 'Mostrar Filtros'}
        </button>
        {mostrarDireito && <PainelDireito />}
      </div>
    </div>
  );
};

export default LayoutPrincipal;
